mapper.select:
SELECT da.id, da.project_id, da.name, da.code, da.is_active, da.is_synchro, da.gitlab_project_id, da.app_template_id, da.uuid FROM devops_application da where da.project_id = #{projectId}
AND da.is_active = TRUE
AND da.is_active = FALSE

SELECT DISTINCT da.id, da.`code`, da.`name` , dam.publish_level, da.project_id projectId FROM devops_application da JOIN devops_app_instance dai ON dai.app_id = da.id JOIN devops_env de ON de.id = dai.env_id left join devops_app_market dam on dam.app_id = da.id WHERE de.project_id = #{projectId} AND de.id = #{envId}
AND dai.`status` = 'running'
AND dai.`status` != 'deleted'

SELECT dam.publish_level publishLevel, dam.contributor, dam.description, da.* FROM devops_application da LEFT JOIN devops_app_market dam ON dam.app_id = da.id WHERE da.is_active AND ( da.project_id = #{projectId} OR ( da.project_id IN
#{project}
AND dam.publish_level = 'organization' ) OR dam.publish_level = 'public' )

