mapper.select:
SELECT dadr.task_name, dadr.last_update_date, dadr.app_id, dadr.env_id, da. NAME AS appName, dav.version, de. NAME AS envName, dai. CODE AS instanceName FROM devops_auto_deploy_record dadr LEFT JOIN devops_application da ON dadr.app_id = da.id LEFT JOIN devops_app_version dav ON dadr.version_id = dav.id LEFT JOIN devops_env de ON dadr.env_id = de.id LEFT JOIN devops_app_instance dai ON dadr.instance_id = dai.id where dadr.project_id = #{projectId}
AND dadr.app_id = #{appId}
AND dadr.env_id = #{envId}
AND dadr.task_name = #{taskName}

sql.if:
AND ( dav.version LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%')
OR dai.code LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%')
)
