mapper.select:
SELECT app_service.id, app_service.name
FROM devops_app_service app_service
         LEFT JOIN (
                   SELECT env_id, app_service_id
                   FROM devops_env_app_service env_app
                   WHERE env_app.env_id = #{envId}
                   ) related_app ON app_service.id = related_app.app_service_id
WHERE app_service.project_id = #{projectId}
  AND app_service.is_failed = FALSE
  AND app_service.is_active = TRUE
  AND app_service.is_synchro = TRUE
  AND related_app.app_service_id IS NULL;

SELECT COUNT(1)
FROM devops_app_service_instance dasi
WHERE dasi.env_id = #{envId} AND dasi.app_service_id = #{appServiceId}

SELECT COUNT(1)
FROM devops_app_service_resource dasr
LEFT JOIN devops_service ds ON dasr.resource_id = ds.id AND dasr.resource_type = 'service'
WHERE dasr.app_service_id = #{appServiceId}
AND ds.env_id = #{envId}

SELECT COUNT(1)
FROM devops_app_service_resource dasr
LEFT JOIN devops_secret ds ON dasr.resource_id = ds.id AND dasr.resource_type = 'secret'
WHERE dasr.app_service_id = #{appServiceId}
AND ds.env_id = #{envId}

SELECT COUNT(1)
FROM devops_app_service_resource dasr
LEFT JOIN devops_ingress di ON dasr.resource_id = di.id AND dasr.resource_type = 'ingress'
WHERE dasr.app_service_id = #{appServiceId}
AND di.env_id = #{envId}

SELECT COUNT(1)
FROM devops_app_service_resource dasr
LEFT JOIN devops_config_map dcm ON dasr.resource_id = dcm.id AND dasr.resource_type = 'configMap'
WHERE dasr.app_service_id = #{appServiceId}
AND dcm.env_id = #{envId}
