mapper.select:
SELECT count(1) FROM ( SELECT DISTINCT ds.id FROM ( SELECT ds.* FROM devops_service ds INNER JOIN devops_env denv ON ds.env_id = denv.id WHERE denv.project_id = #{projectId} AND ds.`status` != 'deleted'
AND ds.env_id = #{envId}
) ds JOIN devops_env de ON ds.env_id = de.id
LEFT JOIN devops_app_resource das ON ds.id = das.resource_id
LEFT JOIN devops_app_service da ON da.id = ds.app_service_id LEFT JOIN devops_service_instance dsai ON dsai.service_id = ds.id LEFT JOIN devops_app_service_instance dai ON dai.id = dsai.instance_id WHERE 1=1
AND dai.id = #{instanceId}
AND das.app_service_id = #{appServiceId} AND das.resource_type='service'
) t

SELECT ds.id, ds.`name`, ds.external_ip, ds.load_balance_ip, ds.ports, ds.`status`, ds.`type`, ds.labels, ds.end_points, da.id AS app_service_id, da.`name` AS app_service_name, de.id AS env_id, de.`name` AS env_name, de.`code` AS namespace, cmd.`status` commandStatus, cmd.command_type commandType, cmd.error error, da.project_id AS appServiceProjectId, dsai.instance_id AS instance_id, dsai.code AS code, dai.`status` AS instance_status FROM ( SELECT devs.* FROM devops_service devs INNER JOIN devops_env denv ON devs.env_id = denv.id WHERE denv.project_id = #{projectId} AND devs.`status` != 'deleted'
AND devs.env_id = #{envId}
ORDER BY ${sort}
) ds JOIN devops_env de ON ds.env_id = de.id
LEFT JOIN devops_app_resource das ON ds.id = das.resource_id
LEFT JOIN devops_env_command cmd ON ds.command_id = cmd.id LEFT JOIN devops_app_service da ON da.id = ds.app_service_id LEFT JOIN devops_service_instance dsai ON dsai.service_id = ds.id LEFT JOIN devops_app_service_instance dai ON dai.id = dsai.instance_id WHERE 1
AND dai.id = #{instanceId}
AND das.app_service_id = #{appServiceId} AND das.resource_type='service'

mapper.update:
UPDATE devops_service ds
SET ds.labels = null
WHERE ds.id = #{serviceId}

UPDATE devops_service ds
SET ds.end_points = null
WHERE ds.id = #{serviceId}

UPDATE devops_service ds
SET ds.external_ip = null
WHERE ds.id = #{serviceId}

mapper.delete:
DELETE FROM devops_service_instance
WHERE service_id IN

    #{serviceId}
