choose.when:
SELECT dp.is_enabled, dp. NAME, dp.trigger_type, dp.created_by, dp.last_update_date, dp.object_version_number, dp.id, EXISTS ( SELECT 1 FROM devops_pipeline_user_rel dpur WHERE dpur.user_id = #{classifyParam.userId} AND dpur.pipeline_id = dp.id AND dp.is_enabled = 1 ) AS execute FROM devops_pipeline dp
JOIN devops_pipeline_stage dps ON dps.pipeline_id = dp.id JOIN devops_pipeline_task dpt ON dpt.stage_id = dps.id JOIN devops_pipeline_app_deploy dpad ON dpad.id = dpt.app_deploy_id AND dpad.env_id IN
#{envId}
WHERE dp.project_id = #{projectId}
AND dp.trigger_type='manual' and dp.created_by = #{classifyParam.userId} UNION

choose.otherwise:
SELECT dp.is_enabled, dp. NAME, dp.trigger_type, dp.created_by, dp.last_update_date, dp.object_version_number, dp.id, EXISTS ( SELECT 1 FROM devops_pipeline_user_rel dpur WHERE dpur.pipeline_id = dp.id AND dpur.user_id = #{classifyParam.userId} AND dp.is_enabled = 1 ) AS EXECUTE FROM devops_pipeline dp
JOIN devops_pipeline_stage dps ON dps.pipeline_id = dp.id JOIN devops_pipeline_task dpt ON dpt.stage_id = dps.id JOIN devops_pipeline_app_deploy dpad ON dpad.id = dpt.app_deploy_id AND dpad.env_id IN
#{envId}
WHERE dp.project_id = #{projectId}
AND dp.trigger_type='manual' and dp.created_by = #{classifyParam.userId}

sql.if:
AND (dp.name LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%')
OR dp.trigger_type LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%')
)

mapper.sql:
SELECT dp.is_enabled, dp. NAME, dp.trigger_type, dp.created_by, dp.last_update_date, dp.object_version_number, dp.id, EXISTS ( SELECT 1 FROM devops_pipeline_user_rel dpur WHERE dpur.pipeline_id = dp.id AND dpur.user_id = #{classifyParam.userId} AND dp.is_enabled = 1 ) AS EXECUTE FROM devops_pipeline dp JOIN devops_pipeline_user_rel dpur ON dpur.pipeline_id = dp.id AND dpur.user_id =#{classifyParam.userId}
JOIN devops_pipeline_stage dps ON dps.pipeline_id = dp.id JOIN devops_pipeline_task dpt ON dpt.stage_id = dps.id JOIN devops_pipeline_app_deploy dpad ON dpad.id = dpt.app_deploy_id AND dpad.env_id IN
#{envId}
WHERE dp.project_id = #{projectId}
